// Clean and structure the form data
const formData = $input.first().json;

// Extract and clean the data
const name = (formData['Full Name'] || '').trim();
const email = (formData.Email || '').trim().toLowerCase();
const jobDescription = (formData['Job Description'] || '').trim();
const resumeFile = formData.Resume;

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(email)) {
  throw new Error('Invalid email format provided');
}

return {
  name: name,
  email: email,
  jobDescription: jobDescription,
  resumeFile: resumeFile,
  fileName: resumeFile?.filename || 'resume',
  fileData: resumeFile?.data || null,
  timestamp: new Date().toISOString()
};

// Extract text from resume file
const inputData = $input.first().json;

// For now, we'll simulate resume text extraction
// In production, you'd want to use a proper PDF/DOC parser
const resumeText = `
EXTRACTED RESUME CONTENT FOR: ${inputData.name}
Email: ${inputData.email}

=== PROFESSIONAL SUMMARY ===
Experienced professional with strong background in various technical and soft skills.

=== WORK EXPERIENCE ===
‚Ä¢ Previous roles and responsibilities
‚Ä¢ Key achievements and contributions
‚Ä¢ Industry experience and expertise

=== EDUCATION ===
‚Ä¢ Educational background
‚Ä¢ Relevant certifications
‚Ä¢ Academic achievements

=== SKILLS ===
‚Ä¢ Technical skills
‚Ä¢ Software proficiency
‚Ä¢ Languages and communication
‚Ä¢ Leadership and teamwork

[Note: This is simulated content. In production, implement actual file parsing]
`;

return {
  ...inputData,
  resumeText: resumeText,
  processingStep: 'resume_extracted'
};

// Parse job analysis response
const inputData = $input.first().json;
const analysisResponse = inputData.message?.content || '{}';

// Try to parse the JSON response
let jobAnalysis;
try {
  // Clean the response and extract JSON
  const cleanResponse = analysisResponse.replace(/```json\n?|```\n?|```/g, '').trim();
  const jsonMatch = cleanResponse.match(/\{[\s\S]*\}/);

  if (jsonMatch) {
    jobAnalysis = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (error) {
  console.log('Failed to parse job analysis, using fallback:', error.message);
  // Fallback analysis structure
  jobAnalysis = {
    technical_skills: ['Communication', 'Problem Solving', 'Team Collaboration'],
    experience_requirements: ['2-3 years experience', 'Bachelor\'s degree preferred'],
    ats_keywords: ['professional', 'experience', 'skills', 'qualifications'],
    core_responsibilities: ['Execute assigned tasks', 'Collaborate with team', 'Meet deadlines'],
    preferred_qualifications: ['Relevant degree', 'Industry experience'],
    industry_terms: ['professional development', 'best practices']
  };
}

// Get previous node data
const previousData = $('Extract Resume Content').first().json;

return {
  ...previousData,
  jobAnalysis: jobAnalysis,
  processingStep: 'job_analyzed'
};

