const inputData = $input.first().json;
const rawResume = inputData.message?.content || inputData.output || 'Resume generation failed';

// Clean up the resume formatting
const cleanResume = rawResume
  .replace(/\*\*/g, '') // Remove markdown bold
  .replace(/\*/g, '‚Ä¢') // Convert asterisks to bullets
  .replace(/#{1,6}\s/g, '') // Remove markdown headers
  .trim();

// Generate filename
const timestamp = new Date().toISOString().split('T')[0];
const safeName = inputData.name ? inputData.name.replace(/[^a-zA-Z0-9]/g, '_') : 'candidate';
const filename = `${safeName}_Optimized_Resume_${timestamp}.txt`;

// Get previous data
const previousData = $('Process Job Analysis').first().json;

return {
  ...previousData,
  optimizedResume: cleanResume,
  filename: filename,
  processingStep: 'resume_generated',
  generatedAt: new Date().toISOString()
};

